                # Check Root account's currency is base
                root = MD_REF.getCurrentAccount().getBook().getRootAccount()
                if root.getCurrencyType() != baseCurr:
                    lNeedFixScript = True

                    txt = "Root account's currency: '%s', Base currency: '%s'" %(root.getCurrencyType(), baseCurr)
