        if MD_REF.getCurrentAccount().getBook() is None: return
        if not (lAdvancedMode): return

        accountsListForOlTxns = AccountUtil.allMatchesForSearch(MD_REF.getCurrentAccount().getBook(), MyAcctFilter(15))
