    def findEnvironmentPassphrases():
        theList = []
        if int(MD_REF.getBuild()) < 3088:     # MD2021.2(3088) added this capability
            return theList
        for k, v in os.environ.items():
            if k.startswith("md_passphrase"):
                theList.append([k,v])
        return theList

